import './Listing.scss'
import Modal from "react-modal";
import {useState} from "react";

// Make sure to bind modal to your appElement (https://reactcommunity.org/react-modal/accessibility/)
Modal.setAppElement('#root');


const customStyles = {
    content: {
        top: '50%',
        left: '50%',
        right: 'auto',
        bottom: 'auto',
        marginRight: '-50%',
        transform: 'translate(-50%, -50%)',
    },
};
export default function Listing({poems}) {

    console.log('incoming poems', poems)
    const [modalIsOpen, setIsOpen] = useState(false);


    function openModal() {
        setIsOpen(true);
    }



    function closeModal() {
        setIsOpen(false);
    }


    return (<>
        <table className="table table-borderless">
            <thead>
            <tr>
                <th scope="col">#</th>
                <th scope="col">Title</th>
                <th scope="col">Author</th>
                <th scope="col">Poems</th>
                <th scope="col">Poem Details</th>
            </tr>
            </thead>
            <tbody>

            {poems?.map((poems, i) => {
                console.log('mapped poems', poems)
                return (<>
                    <tr>
                        <th scope="row">{i + 1}</th>
                        <td>{poems?.title}</td>
                        <td>{poems?.author}</td>
                        <td>{poems?.lines?.slice(0, 5)} -...</td>
                        <td><i className="gg-file-document"></i></td>
                    </tr>
                </>)
            })}
            </tbody>
        </table>
        <div>
            <button onClick={openModal}>Open Modal</button>
            <Modal
                isOpen={modalIsOpen}
                // onAfterOpen={afterOpenModal}
                onRequestClose={closeModal}
                style={customStyles}
                contentLabel="Example Modal"
            >
                <button onClick={closeModal}>close</button>
                <div>I am a modal</div>
                <form>
                    <input />
                    <button>tab navigation</button>
                    <button>stays</button>
                    <button>inside</button>
                    <button>the modal</button>
                </form>
            </Modal>
        </div>
    </>)
}
