import './Listing.scss'
import {useState} from "react";
import Modal from "../Modal/Modal";
import {useRecoilState} from "recoil";
import {darkModeState} from "../../shared/GlobalState";


const BUTTON_WRAPPER_STYLES = {
    position: 'relative', zIndex: 1
}

export default function Listing({poems}) {

    const [isOpen, setIsOpen] = useState(false);
    const [singlePoem, setSinglePoem] = useState('')

    return (<>
        <table className="table table-borderless">
            <thead>
            <tr>
                <th scope="col">#</th>
                <th scope="col">Title</th>
                <th scope="col">Author</th>
                <th scope="col">Poems</th>
                <th scope="col">Poem Details</th>
            </tr>
            </thead>
            <tbody>
            <>
                {poems?.map((poem, i) => {
                    return (<tr key={poem.lines.toString().length}>
                            <th scope="row">{i + 1}</th>
                            <td>{poem?.title}</td>
                            <td>{poem?.author}</td>
                            <td>{poem?.lines?.slice(0, 5)} -...</td>
                            <div style={BUTTON_WRAPPER_STYLES}>
                                <button
                                    onClick={() => {
                                        setIsOpen(true);
                                        setSinglePoem(poem)
                                    }}
                                >Open Modal
                                </button>
                                <Modal poemSingleData={singlePoem} open={isOpen} onClose={() => setIsOpen(false)}>

                                </Modal>
                            </div>
                        </tr>

                    )
                })}
            </>
            </tbody>
        </table>

    </>)
}
