import './Navbar.scss'
import Listing from "../Listing/Listing";
import Dropdown from "../Dropdown/Dropdown";
import SearchInput from "../SearchInput/SearchInput";
import Button from "../Button/Button";
import NavbarBrand from "../NavbarBrand/NavbarBrand";
import {useEffect, useState} from "react";
import axios from '../../utils/axios'
import Loader from "../Loader/Loader";
import Modal from 'react-modal';

Modal.setAppElement('#yourAppElement');

const customStyles = {
    content: {
        top: '50%',
        left: '50%',
        right: 'auto',
        bottom: 'auto',
        marginRight: '-50%',
        transform: 'translate(-50%, -50%)',
    },
};

export default function Navbar() {

    const [poems, setPoems] = useState([])
    const [isLoading, setIsLoading] = useState(true)
    const [authors, setAuthors] = useState([])
    const [titles, setTitles] = useState([])


    function openModal() {
        setIsOpen(true);
    }


    function afterOpenModal() {
        // references are now sync'd and can be accessed.
        subtitle.style.color = '#f00';
    }

    function closeModal() {
        setIsOpen(false);
    }   

    const getAuthors = async () => {

        try {
            let response = await axios.get('author')
            setAuthors(response?.data?.authors)
        } catch (error) {
            console.log(error)
        }
    }

    const getPoemsTitle = async () => {

        try {
            let response = await axios.get('title')
            setTitles(response?.data?.titles)
        } catch (error) {
            console.log(error)
        }
    }

    const fetch20Poems = async (event) => {
        event.preventDefault()

        try {
            let response = await axios.get('random/20')
            setPoems(response?.data)
            setIsLoading(false)
        } catch (error) {
            console.log(error)
        }
    }

    const filterByArtistAndTitle = async () => {

        try {
            let response = await axios.get('')
        } catch (error) {
            console.log(error)
        }
    }

    const filterByAuthor = async (event) => {

        try {
            let response = await axios.get(`author/${event}`)
            setPoems(response?.data)
            setIsLoading(false)
        } catch (error) {
            console.log(error)
        }
    }

    function debounce(func, timeout = 300) {
        let timer;

        return (...args) => {
            clearTimeout(timer);
            timer = setTimeout(() => {
                func.apply(this, args);
            }, timeout);
        };
    }

    const filterByTitle = async (event) => {

        try {
            let response = await axios.get(`title/${event}`)
            setPoems(response?.data)
            setIsLoading(false)
        } catch (error) {
            console.log(error)
        }
    }

    const onArtistChangeDebounce = debounce((event) => filterByAuthor(event));
    const onTitleChangeDebounce = debounce((event) => filterByTitle(event));

    useEffect(() => {
        getAuthors();
        getPoemsTitle();
    }, [])

    return (
        <>
            <nav className="Navbar navbar navbar-expand-lg navbar-light bg-light">
                <div className="container-fluid">
                    <NavbarBrand brandName="Poems"/>
                    <form className="element-wrapper">
                        <Dropdown name="Sort By Author" poemsData={authors} onChangeState={onArtistChangeDebounce}/>
                        <Dropdown name="Sort By Title" poemsData={titles} onChangeState={onTitleChangeDebounce}/>
                        <SearchInput/>
                        <Button name="Fetch 20 poems" type="button" onClickFunction={fetch20Poems}/>
                    </form>
                </div>
            </nav>
            {
                isLoading ? <Loader/> :
                    <div className="list-wrapper">
                        <Listing poems={poems}/>
                    </div>
            }
        </>


    )
}
