import './Navbar.scss'
import Listing from "../Listing/Listing";
import Dropdown from "../Dropdown/Dropdown";
import SearchInput from "../SearchInput/SearchInput";
import Button from "../Button/Button";
import NavbarBrand from "../NavbarBrand/NavbarBrand";
import {useEffect, useState} from "react";
import axios from '../../utils/axios'
import Loader from "../Loader/Loader";
import Modal from 'react-modal';

// Make sure to bind modal to your appElement (https://reactcommunity.org/react-modal/accessibility/)
Modal.setAppElement('#yourAppElement');


const customStyles = {
    content: {
        top: '50%',
        left: '50%',
        right: 'auto',
        bottom: 'auto',
        marginRight: '-50%',
        transform: 'translate(-50%, -50%)',
    },
};

export default function Navbar() {
    let subtitle;

    const [poems, setPoems] = useState([])
    const [isLoading, setIsLoading] = useState(true)
    const [authors, setAuthors] = useState([])
    const [titles, setTitles] = useState([])
    const [modalIsOpen, setIsOpen] = useState(false);


    function openModal() {
        setIsOpen(true);
    }



    function closeModal() {
        setIsOpen(false);
    }

    const getAuthors = async () => {

        try {
            let response = await axios.get('author')
            setAuthors(response?.data?.authors)
        } catch (error) {
            console.log(error)
        }
    }

    const getPoemsTitle = async () => {

        try {
            let response = await axios.get('title')
            setTitles(response?.data?.titles)
        } catch (error) {
            console.log(error)
        }
    }

    const fetch20Poems = async (event) => {
        event.preventDefault()

        try {
            let response = await axios.get('random/20')
            setPoems(response?.data)
            setIsLoading(false)
        } catch (error) {
            console.log(error)
        }
    }

    const filterByArtistAndTitle = async () => {

        try {
            let response = await axios.get('')
        } catch (error) {
            console.log(error)
        }
    }

    const filterByAuthor = async (event) => {

        try {
            let response = await axios.get(`author/${event}`)
            setPoems(response?.data)
            setIsLoading(false)
        } catch (error) {
            console.log(error)
        }
    }

    function debounce(func, timeout = 300) {
        let timer;

        return (...args) => {
            clearTimeout(timer);
            timer = setTimeout(() => {
                func.apply(this, args);
            }, timeout);
        };
    }

    const filterByTitle = async (event) => {

        try {
            let response = await axios.get(`title/${event}`)
            setPoems(response?.data)
            setIsLoading(false)
        } catch (error) {
            console.log(error)
        }
    }

    const onArtistChangeDebounce = debounce((event) => filterByAuthor(event));
    const onTitleChangeDebounce = debounce((event) => filterByTitle(event));

    useEffect(() => {
        getAuthors();
        getPoemsTitle();
    }, [])

    return (
            <div>
                <button onClick={openModal}>Open Modal</button>
                <Modal
                    isOpen={modalIsOpen}
                    onRequestClose={closeModal}
                    style={customStyles}
                    contentLabel="Example Modal"
                >
                    <h2 ref={(_subtitle) => (subtitle = _subtitle)}>Hello</h2>
                    <button onClick={closeModal}>close</button>
                    <div>I am a modal</div>
                    <form>
                        <input />
                        <button>tab navigation</button>
                        <button>stays</button>
                        <button>inside</button>
                        <button>the modal</button>
                    </form>
                </Modal>
            </div>




    )
}
