import ReactDom from "react-dom";
import './Modal.scss'
import Listing from "../Listing/Listing";
import {useState} from "react";

const MODAL_STYLES = {
    position: 'fixed',
    top: '50%',
    left: '50%',
    transform: 'translate(-50%, -50%)',
    backgroundColor: '#FFF',
    padding: '50px',
    zIndex: 1000,
    height: '600px',
    overflow: 'auto',
    borderRadius: '20px',

}

const OVERLAY_STYLES = {
    position: 'fixed',
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    backgroundColor: 'rgba(204, 204, 204, 0.8)',
    zIndex: 1000,
}

export default function Modal({open, onClose, poemSingleData, poems, isOpen, setIsOpen}) {

    const [testz, setTest] = useState(poems)


    if (!open) return null
    console.log('ZH', poems)

    if (poemSingleData) {
        return ReactDom.createPortal(
            <>
                <div className="Modal">
                    <div style={OVERLAY_STYLES}/>
                    <div style={MODAL_STYLES}>
                        <div className="button-wrapper">
                            <button className="button-close-modal" onClick={onClose}>X
                            </button>
                        </div>
                        <div className="wrapper">
                            <h4>{poemSingleData?.title}</h4>
                            <h5>{poemSingleData?.author}</h5>
                            <p>{poemSingleData?.lines}</p>
                        </div>
                    </div>
                </div>
            </>,
            document.getElementById('portal')
        )
    } else {
        let test  = testz

        console.log('TESTTTTT',test)

        return ReactDom.createPortal(
            <>
                <h1>asdasd</h1>
                <div className="Modal">
                    <div style={OVERLAY_STYLES}/>
                    <div style={MODAL_STYLES}>
                        <div className="button-wrapper">
                            <button className="button-close-modal" onClick={onClose}>X
                            </button>
                        </div>
                        <div className="wrapper">
                            {
                                poems?.map((el,) => {
                                    return (
                                        <p>{el?.title}</p>
                                    )
                                })
                            }
                        </div>
                    </div>
                </div>
            </>,
            document.getElementById('portal')
        )
    }


}
