import './Listing.scss'
import Modal from "react-modal";
import {useState} from "react";
import ModalPoems from "../ModalPoems/ModalPoems";

// Make sure to bind modal to your appElement (https://reactcommunity.org/react-modal/accessibility/)
Modal.setAppElement('#root');


const customStyles = {
    content: {
        top: '50%',
        left: '50%',
        right: 'auto',
        bottom: 'auto',
        marginRight: '-50%',
        transform: 'translate(-50%, -50%)',
    },
};

export default function Listing({poems}) {

    console.log('incoming poems', poems)
    const [modalIsOpen, setIsOpen] = useState(false);
    const [selectedPoem, setSelectedPoem] = useState(null);

    function openModal(poem) {
        // setSelectedPoem(poem);
        setTimeout(()=>{
            setIsOpen(!modalIsOpen);
        }, 100)

    }

    return (<>
        <table className="table table-borderless">
            <thead>
            <tr>
                <th scope="col">#</th>
                <th scope="col">Title</th>
                <th scope="col">Author</th>
                <th scope="col">Poems</th>
                <th scope="col">Poem Details</th>
            </tr>
            </thead>
            <tbody>
            <>
            {poems?.map((poem, i) => {
                console.log('mapped poems', poems)
                return (

                        <tr key={poem.lines.toString() + poem?.author.toString()}>
                            <th scope="row">{i + 1}</th>
                            <td>{poem?.title}</td>
                            <td>{poem?.author}</td>
                            <td>{poem?.lines?.slice(0, 5)} -...</td>
                            <td><i className="gg-file-document" onClick={openModal}></i>
                                <div>
                                   <Modal
                                        isOpen={modalIsOpen}
                                        style={customStyles}
                                        contentLabel="Example Modal"
                                    >
                                        <button onClick={(_)=>openModal(poem)}>close</button>
                                         <ModalPoems singlePoem={poems[i]}/>go
                                    </Modal>
                                </div>
                            </td>
                        </tr>

                )
            })}
                </>
            </tbody>
        </table>

    </>)
}
