import './Listing.scss'
import Modal from "react-modal";
import {useState} from "react";
import ModalPoems from "../ModalPoems/ModalPoems";

// Make sure to bind modal to your appElement (https://reactcommunity.org/react-modal/accessibility/)
Modal.setAppElement('#root');


const BUTTON_WRAPPER_STYLES = {
    position: 'relative',
    zIndex: 1
}

const OTHER_CONTENT_STYLES = {
    position: 'relative',
    zIndex: 2,
    backgroundColor: 'red',
    padding: '10px'
}


export default function Listing({poems}) {

    console.log('incoming poems', poems)
    const [isOpen, setIsOpen] = useState(false);
    const [selectedPoem, setSelectedPoem] = useState(null);

    return (<>
        <table className="table table-borderless">
            <thead>
            <tr>
                <th scope="col">#</th>
                <th scope="col">Title</th>
                <th scope="col">Author</th>
                <th scope="col">Poems</th>
                <th scope="col">Poem Details</th>
            </tr>
            </thead>
            <tbody>
            <>
                {poems?.map((poem, i) => {
                    return (

                        <tr key={poem.lines.toString() + poem?.author.toString()}>
                            <th scope="row">{i + 1}</th>
                            <td>{poem?.title}</td>
                            <td>{poem?.author}</td>
                            <td>{poem?.lines?.slice(0, 5)} -...</td>
                            <div style={BUTTON_WRAPPER_STYLES}  onClick={() => console.log('clicked')}>
                                <button onClick={() => setIsOpen(true)}>Open Modal</button>
                                {/*<ModalPoems open={isOpen} onClose={() => setIsOpen(false)}>*/}
                                    <td>{poem?.lines?.slice(0, 5)} -...</td>
                                {/*</ModalPoems>*/}
                            </div>
                        </tr>

                    )
                })}
            </>
            </tbody>
        </table>

    </>)
}
