import './Listing.scss'
import {useState} from "react";
import Modal from "../Modal/Modal";
import {useRecoilState} from "recoil";
import {darkModeState} from "../../shared/GlobalState";
import Loader from "../Loader/Loader";


const BUTTON_WRAPPER_STYLES = {
    position: 'relative', zIndex: 1
}

export default function Listing({poems, isLoading, favouritePoems, setFavouritePoems}) {



    const addToPoems = (poems) => {
        let favPoemsArray = []
        favPoemsArray.push(poems)

        favPoemsArray?.forEach((poemsInsideArray, i) => {
            if (favouritePoems.includes(poemsInsideArray) === false) {
                setFavouritePoems(favouritePoems => [...favouritePoems, poemsInsideArray])
            }
        })
    }

    const deletePoems = (deletedPoems) => {
        setFavouritePoems(favouritePoems.filter((item) => item !== deletedPoems));
    }

    console.log('deleted', favouritePoems)


    const data = favouritePoems?.map((el, i) => {
        console.log('eheh', el)
    })

    return (<>
        {isLoading ? <Loader/> :
            <div className="Listing">
                <p>{data}</p>
                <table className="table table-borderless">
                    <thead>
                    <tr>
                        <th scope="col">#</th>
                        <th scope="col">Title</th>
                        <th scope="col">Author</th>
                        <th scope="col-2">Poems</th>
                        <th scope="col">Poem Details</th>
                        <th scope="col">Favourites</th>
                    </tr>
                    </thead>
                    <tbody>
                    {poems?.map((poem, i) => {
                        return (<tr key={poem.lines.toString().length}>
                                <th scope="row">{i + 1}</th>
                                <td>{poem?.title}</td>
                                <td>{poem?.author}</td>
                                <td>{poem?.lines?.slice(0, 5)} -...</td>
                                <div style={BUTTON_WRAPPER_STYLES}>
                                    <button
                                        className="modal-open-button"
                                        onClick={() => {
                                            setIsOpen(true);
                                            setSinglePoem(poem)
                                        }}
                                    >Open Modal
                                    </button>
                                    <Modal
                                        poemSingleData={singlePoem}
                                        open={isOpen}
                                        onClose={() => setIsOpen(false)}
                                    />
                                </div>
                                <td>
                                    <button type="button" onClick={() => addToPoems(poem)}
                                            className="add-to-favourites">
                                        +
                                    </button>
                                </td>
                                <td>
                                    <button type="button" onClick={() => deletePoems(poem)}
                                            className="add-to-favourites">
                                        -
                                    </button>
                                </td>
                            </tr>
                        )
                    })}
                    </tbody>
                </table>
            </div>
        }
    </>)
}
