    padding: '50px',
    zIndex: 1000,


}


export default function ModalPoems ({open, children, onClose}) {

    console.log('open', open, 'children', children)

    if(!open) return null

    return (
        <div style={CONST_STYLES}>
            <button onClick={onClose}>Close</button>
            <div>{children}</div>
        </div>
    )
}
