        }, 100)

    }

    return (<>
        <table className="table table-borderless">
            <thead>
            <tr>
                <th scope="col">#</th>
                <th scope="col">Title</th>
                <th scope="col">Author</th>
                <th scope="col">Poems</th>
                <th scope="col">Poem Details</th>
            </tr>
            </thead>
            <tbody>
            <>
                {poems?.map((poem, i) => {
                    return (

                        <tr key={poem.lines.toString() + poem?.author.toString()}>
                            <th scope="row">{i + 1}</th>
                            <td>{poem?.title}</td>
                            <td>{poem?.author}</td>
                            <td>{poem?.lines?.slice(0, 5)} -...</td>
                            <div style={BUTTON_WRAPPER_STYLES} >
                                <button>Open Modal</button>

                                <ModalPoems open={isOpen}>
                                    Fancy Modal
                                </ModalPoems>
                            </div>
                        </tr>

                    )
                })}
            </>
            </tbody>
        </table>

    </>)
}
